package ch1.ch1_7;

public class WelcomeWithThreeMessages {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Programming is fun!");
		System.out.println("Fundamentals First");
		// this is to print multiple statement in different line
		// use System.out.print("") to get the same line
		
		System.out.print("Problem Driven");
		System.out.println(" This would appear right after the previous statement!");
		// note that the next println affect the next statement
		System.out.println();
		// it can also be used to create empty line
		System.out.println("There should be an empty line above this.");
	}

}
